net.rptools.maptool.model.MacroButtonPro.command:
[h: selectedToken = getSelected()]

[h, if(listCount(selectedToken) > 1), CODE:
	{
		[h: broadcast("Please select 1 token.")]
		[h: abort(0)]
	};
	{

	}
]

[h: licenseProp = getProperty("license", selectedToken)]
[h: licenseProp = if(licenseProp == "", "<b>This token has no associated licensing.<b>", licenseProp)]

[h: tokenName = getName(selectedToken)]

[frame5("Licensing"):
	{
	  <html>
		<head>
		  <title>Licensing for [r:tokenName]</title>
		</head>
		<body>
			[r: licenseProp]
		</body>
	  </html>
	}
]

[h: selectedTokens = getSelected()]

[h, FOREACH(tokID, selectedTokens), CODE:
	{
		[h: resetProperty("License", tokID)]
		[h: tokName = getName(tokID)]
		[h: broadcast("<b>"+tokName+"</b> License has been removed.")]		
	}
]
